import React, { Component } from 'react';
import './App.css';
import ParticlesBg from 'particles-bg';
import Navigation from './Components/Navigation/Navigation'
import FaceRecognition from './Components/FaceRecognition/FaceRecognition'
import Logo from './Components/Logo/Logo';
import ImageLinkForm from './Components/ImageLinkForm/ImageLinkForm'
import Rank from './Components/Rank/Rank'
import "./App.css";



///////////////////////////////////////////////////////////////////////////////////////////////////
// In this section, we set the user authentication, user and app ID, model details, and the URL
// of the image we want as an input. Change these strings to run your own example.
//////////////////////////////////////////////////////////////////////////////////////////////////

const returnClarifaiReqOptions = (imageUrl) => {

  const PAT = '4f7d0da0bca240efb07ae6b755858b5e';
// Specify the correct user_id/app_id pairings
// Since you're making inferences outside your app's scope
  const USER_ID = 'mshaik791';       
  const APP_ID = 'face-detection';
  // Change these to whatever model and image URL you want to use
  const MODEL_ID = 'face-detection';
  const MODEL_VERSION_ID = '6dc7e46bc9124c5c8824be4822abe105';    
  const IMAGE_URL = imageUrl;

  return raw = JSON.stringify({
    "user_app_id": {
        "user_id": USER_ID,
        "app_id": APP_ID
    },
    "inputs": [
        {
            "data": {
                "image": {
                    "url": IMAGE_URL
                }
            }
        }
    ]
  });

  const requestOptions = {
    method: 'POST',
    headers: {
        'Accept': 'application/json',
        'Authorization': 'Key ' + PAT
    },
    body: raw



  };


  return requestOptions;




}

// Your PAT (Personal Access Token) can be found in the portal under Authentification

///////////////////////////////////////////////////////////////////////////////////
// YOU DO NOT NEED TO CHANGE ANYTHING BELOW THIS LINE TO RUN THIS EXAMPLE
///////////////////////////////////////////////////////////////////////////////////



// NOTE: MODEL_VERSION_ID is optional, you can also call prediction with the MODEL_ID only
// https://api.clarifai.com/v2/models/{YOUR_MODEL_ID}/outputs
// this will default to the latest version_id



class App extends Component {

 constructor(){

  super();
  this.state = {
    input: '',
    imageUrl: ''


  }


 }

 onButtonSubmit = () => {
    this.setState({imageUrl: this.state.input})

    fetch("https://api.clarifai.com/v2/models/" + 'face-detection' + "/outputs", returnClarifaiReqOptions(this.state.input))
    .then(response => response.json())
    .then(result => console.log(result))
    .catch(error => console.log('error', error));

    console.log('click')

 }

 onInputChange = (event) => {

    this.setState({input: event.target.value });

 }

  render() {


    return (

      <div className="App">

          <ParticlesBg type="cobweb" bg={true} />
          <Navigation/>
          <Logo/>
          <Rank/>
          <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>
          <FaceRecognition imageUrl={this.state.imageUrl}/>

          {/*
                  <ImageLinkForm />
                  <FaceRecognition/>*/}

      </div>
    );

  }


}

export default App;
